library(shiny)

# source chapters
source("R/chapters/ch00_welcome.R", local = TRUE)
source("R/chapters/ch01_selection_response.R", local = TRUE)

server <- function(input, output, session) {
  
  # start modules
  ch00_server("ch00")
  ch01_server("ch01")
  
  # render selected chapter UI in the MAIN area
  output$chapter_main <- renderUI({
    switch(input$chapter,
           "ch00" = ch00_ui("ch00"),
           "ch01" = ch01_ui("ch01")
    )
  })
}

ch00_ui <- function(id) {
  ns <- NS(id)
  tagList(
    uiOutput(ns("welcome_text"))
  )
}

ch00_server <- function(id) {
  moduleServer(id, function(input, output, session) {
    
    output$welcome_text <- renderUI({
      tagList(
        tags$h2("Welcome to the Interactive Course"),
        tags$p("The chapters in this course will be added one by one."),
        tags$p("This website is currently under development and the work is in progress.")
      )
    })
    
  })
}

ch01_ui <- function(id) {
  ns <- NS(id)
  
  tagList(
    uiOutput(ns("content"))
  )
}


ch01_server <- function(id) {
  moduleServer(id, function(input, output, session) {
    
    output$content <- renderUI({
      
      tagList(
        
        # Chapter title
        tags$h2("Chapter 1 — Selection Response"),
        
        # Definition text
        tags$p(
          "Selection response describes the expected change in the population mean ",
          "for a trait after one cycle of selection. It quantifies how much genetic ",
          "improvement is achieved when superior individuals are selected and used ",
          "as parents for the next generation."
        ),
        
        tags$p(
          "The magnitude of response depends primarily on selection intensity, ",
          "heritability, and the phenotypic variation present in the population."
        ),
        
        # Formula box
        tags$div(
          style = "
            border: 2px solid #2c3e50;
            border-radius: 8px;
            padding: 20px;
            margin-top: 20px;
            background-color: #f8f9fa;
            text-align: center;
            font-size: 20px;
            font-weight: 500;
          ",
          
          HTML("R = h<sup>2</sup> × S")
        ),
        
        tags$br(),
        
        tags$div(
          style = "
            border-left: 4px solid #6c757d;
            padding-left: 12px;
            color: #555;
          ",
          tags$p(HTML("<b>Where:</b>")),
          tags$p(HTML("R = response to selection")),
          tags$p(HTML("h<sup>2</sup> = narrow-sense heritability")),
          tags$p(HTML("S = selection differential"))
        )
        
      )
    })
  })
}
